cho date('d M,Y', $date);									?> at									<?php 									$time = ($row->update_time); 									echo date('H:i', $time);									?>								</td>								<td class="text-right">									<div class="btn-group">										<a href="edit/<?= $row->id; ?>" class="btn-white btn btn-xs">Edit										</a>										<a href="javascript:void(0)" onclick="delete_rec('<?= $row->id; ?>')" class="btn-white btn btn-xs">Delete</i> </a>									</div>								</td>							</tr>							<?php							}							?>						</tbody>					</table>				</div>			</div>		</div>	</div></div><script>var delete_rec1 = 0;function delete_rec(id){	if (confirm('Are you sure Delete?')) { 	if(delete_rec1==0)	{		delete_rec1 = 1;		$.ajax({			type       : "POST",			data       :  { id : id ,} ,			url        : "<?= base_url()?>admin/<?= $Page_name; ?>/delete_rec",			success    : function(data){					if(data!="")					{						java_alert_function("success","Delete Successfully");						$("#row_"+id).hide("500");					}										else					{						java_alert_function("error","Something Wrong")					}					delete_rec1 = 0;				}			});		}	}}</script>